package com.peter.controller;import com.peter.entities.Dept;import com.peter.service.DeptClientService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;/** * @ClassName DeptController_Consumer * @Description TODO * @Auther peter * @Date 2019-06-09 23:50 * @Version 1.1.0 */@RestControllerpublic class DeptController_Consumer {    @Autowired    private DeptClientService deptClientService;    @RequestMapping(value = "/consumer/dept/add")    public boolean add(Dept dept) {        return this.deptClientService.add(dept);    }    @RequestMapping(value = "/consumer/dept/get/{id}")    public Dept get(@PathVariable("id") Long id) {        return this.deptClientService.get(id);    }    @SuppressWarnings("unchecked")    @RequestMapping(value = "/consumer/dept/list")    public List<Dept> list() {        return this.deptClientService.list();    }}