package com.peter.sevice.impl;import com.peter.dao.DeptDao;import com.peter.entities.Dept;import com.peter.sevice.DeptService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @ClassName DeptSeviceImpl * @Description TODO * @Auther peter * @Date 2019-06-09 23:23 * @Version 1.1.0 */@Servicepublic class DeptSeviceImpl implements DeptService {    @Autowired    private DeptDao dao;    @Override    public boolean add(Dept dept) {        return dao.addDept(dept);    }    @Override    public Dept get(Long id) {        return dao.findById(id);    }    @Override    public List<Dept> list() {        return dao.findAll();    }}