package com.peter.controller;import com.peter.entities.Dept;import com.peter.sevice.DeptService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;/** * @ClassName DeptController * @Description TODO * @Auther peter * @Date 2019-06-09 23:28 * @Version 1.1.0 */@RestControllerpublic class DeptController{    @Autowired    private DeptService service;    @RequestMapping(value="/dept/add",method= RequestMethod.POST)    public boolean add(@RequestBody Dept dept)    {        return service.add(dept);    }    @RequestMapping(value="/dept/get/{id}",method=RequestMethod.GET)    public Dept get(@PathVariable("id") Long id)    {        return service.get(id);    }    @RequestMapping(value="/dept/list",method=RequestMethod.GET)    public List<Dept> list()    {        return service.list();    }}